import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;

    // Filter out noisy requests from console logs
    const shouldSkipLogging = (
      req.method === "HEAD" ||                    // Skip HEAD requests (health checks)
      path === "/api/health" ||                   // Skip health endpoint
      path.startsWith("/api/health/")             // Skip diagnostic endpoints
    );

    if (path.startsWith("/api") && !shouldSkipLogging) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.

  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { travelApiService } from "./services/travel-api";
import {
  initializeCacheService,
  getCacheServiceInstance,
} from "./services/cache-service";
import { storage } from "./storage";
import { travelSearchSchema } from "@shared/schema";
import { ZodError } from "zod";
import { claudeService } from "./services/claude-service";
import { getCountriesForRegion } from "./data/regionCountries";

export async function registerRoutes(app: Express): Promise<Server> {
  // Initialize cache service
  try {
    await initializeCacheService();
    console.log("Cache service initialized successfully");
  } catch (error) {
    console.error("Failed to initialize cache service:", error);
  }

  // Mock data initialization removed - using live APIs only

  // Progressive travel search endpoint
  app.get("/api/travel/search/progressive", async (req, res) => {
    try {
      // Parse and validate query parameters
      const queryParams = {
        budget: req.query.budget ? Number(req.query.budget) : undefined,
        origin: req.query.origin as string | undefined,
        nights: req.query.nights ? Number(req.query.nights) : undefined,
        month: req.query.month ? Number(req.query.month) : undefined,
        region: req.query.region as string | undefined,
        country: req.query.country as string | undefined,
        travelStyle: req.query.travelStyle as "budget" | "mid" | "luxury" | undefined,
        sessionId: req.query.sessionId as string | undefined,
        includeEstimates:
          req.query.includeEstimates === "false"
            ? false
            : req.query.includeEstimates === "true"
              ? true
              : true,
      };

      // Remove undefined values and validate
      const cleanParams = Object.fromEntries(
        Object.entries(queryParams).filter(([_, v]) => v !== undefined),
      );

      const validatedParams = travelSearchSchema.parse(cleanParams);

      // Using Claude AI for all cost estimates

      // Call progressive search service
      // Check cache first for immediate results
      const cache = getCacheServiceInstance();
      const cached = await cache.get(validatedParams);
      if (cached) {
        return res.json({
          success: true,
          sessionId: "cached",
          status: "completed",
          progress: {
            processed: cached.results.length,
            total: cached.results.length,
            percentage: 100,
          },
          results: cached.results,
          countries: cached.countries,
          totalResults: cached.results.length,
          timestamp: new Date().toISOString(),
        });
      }

      // Call progressive search service
      const results =
        await travelApiService.getProgressiveRecommendations(validatedParams);

      res.json({
        success: true,
        ...results,
        timestamp: new Date().toISOString(),
      });
    } catch (error) {
      if (error instanceof ZodError) {
        return res.status(400).json({
          message: "Invalid search parameters",
          errors: error.errors,
          timestamp: new Date().toISOString(),
        });
      }

      console.error("Progressive search error:", error);
      res.status(500).json({
        message: "Internal server error during progressive search",
        error: error instanceof Error ? error.message : "Unknown error",
        timestamp: new Date().toISOString(),
      });
    }
  });

  // Progressive search polling endpoint
  app.get("/api/travel/search/progressive/:sessionId", async (req, res) => {
    try {
      const sessionId = req.params.sessionId;

      if (!sessionId) {
        return res.status(400).json({
          message: "Session ID is required",
          timestamp: new Date().toISOString(),
        });
      }

      // Get the original search parameters from query (for session validation)
      const queryParams = {
        budget: req.query.budget ? Number(req.query.budget) : undefined,
        origin: req.query.origin as string | undefined,
        nights: req.query.nights ? Number(req.query.nights) : undefined,
        month: req.query.month ? Number(req.query.month) : undefined,
        region: req.query.region as string | undefined,
        country: req.query.country as string | undefined,
        includeEstimates:
          req.query.includeEstimates === "false"
            ? false
            : req.query.includeEstimates === "true"
              ? true
              : true,
      };

      // Remove undefined values and validate
      const cleanParams = Object.fromEntries(
        Object.entries(queryParams).filter(([_, v]) => v !== undefined),
      );

      const validatedParams = travelSearchSchema.parse(cleanParams);

      // Handle "cached" sessionId - return cached results directly
      if (sessionId === "cached") {
        const cache = getCacheServiceInstance();
        const cached = await cache.get(validatedParams);
        if (cached) {
          return res.json({
            sessionId: "cached",
            status: "completed",
            progress: {
              processed: cached.results.length,
              total: cached.results.length,
              percentage: 100,
            },
            results: cached.results,
            countries: cached.countries,
            totalResults: cached.results.length,
            timestamp: new Date().toISOString(),
          });
        }
      }

      // Call progressive search service (which handles existing sessions)
      const results =
        await travelApiService.getProgressiveRecommendations({
          ...validatedParams,
          sessionId: sessionId,
        });

      res.json(results);
    } catch (error) {
      if (error instanceof ZodError) {
        return res.status(400).json({
          message: "Invalid polling parameters",
          errors: error.errors,
          timestamp: new Date().toISOString(),
        });
      }

      console.error("Progressive polling error:", error);
      res.status(500).json({
        message: "Internal server error during progressive polling",
        error: error instanceof Error ? error.message : "Unknown error",
        timestamp: new Date().toISOString(),
      });
    }
  });

  // Legacy travel recommendations endpoint (for backwards compatibility)
  app.get("/api/travel/recs", async (req, res) => {
    try {
      // Parse and validate query parameters
      const queryParams = {
        budget: req.query.budget ? Number(req.query.budget) : undefined,
        origin: req.query.origin as string | undefined,
        nights: req.query.nights ? Number(req.query.nights) : undefined,
        month: req.query.month ? Number(req.query.month) : undefined,
        region: req.query.region as string | undefined,
        country: req.query.country as string | undefined,
        limit: req.query.limit ? Number(req.query.limit) : undefined,
        page: req.query.page ? Number(req.query.page) : undefined,
        sort: req.query.sort as string | undefined,
        includeEstimates:
          req.query.includeEstimates === "false"
            ? false
            : req.query.includeEstimates === "true"
              ? true
              : true, // Default to true
      };

      // Remove undefined values
      const cleanParams = Object.fromEntries(
        Object.entries(queryParams).filter(([_, v]) => v !== undefined),
      );

      const validatedParams = travelSearchSchema.parse(cleanParams);

      // Using Claude AI for all cost estimates

      const recommendations =
        await travelApiService.getRecommendations(validatedParams);

      res.json(recommendations);
    } catch (error) {
      if (error instanceof ZodError) {
        return res.status(400).json({
          message: "Invalid query parameters",
          errors: error.errors,
        });
      }

      console.error("Error getting travel recommendations:", error);
      res.status(500).json({
        message: "Internal server error",
        error: error instanceof Error ? error.message : "Unknown error",
      });
    }
  });

  // Airport search endpoint - Claude-based intelligent matching
  app.get("/api/airports/search", async (req, res) => {
    try {
      const query = req.query.q as string;

      if (!query || query.length < 2) {
        return res.json({
          success: true,
          query: query || "",
          results: [],
          count: 0,
          timestamp: new Date().toISOString()
        });
      }

      const searchResults = await claudeService.searchAirports(query);
      res.json(searchResults);
    } catch (error) {
      console.error("Airport search error:", error);
      res.status(500).json({
        success: false,
        query: req.query.q as string || "",
        results: [],
        count: 0,
        timestamp: new Date().toISOString(),
        error: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  // Health check endpoint
  app.get("/api/health", (req, res) => {
    res.json({
      status: "ok",
      timestamp: new Date().toISOString(),
      useMock: process.env.USE_MOCK !== "false",
    });
  });

  // Cache metrics endpoint
  app.get("/api/cache/metrics", async (req, res) => {
    try {
      const cache = getCacheServiceInstance();
      const metrics = cache.getMetrics();
      const config = cache.getConfig();

      res.json({
        metrics,
        config: {
          ttlMinutes: config.ttl / (60 * 1000),
          maxSize: config.maxSize,
          checkPeriodMinutes: config.checkPeriod / (60 * 1000),
        },
        currentSize: await cache.getSize(),
        timestamp: new Date().toISOString(),
      });
    } catch (error) {
      res.status(500).json({
        message: "Cache service not available",
        error: error instanceof Error ? error.message : "Unknown error",
        timestamp: new Date().toISOString(),
      });
    }
  });

  // Cache clear endpoint (for admin/testing)
  app.post("/api/cache/clear", async (req, res) => {
    try {
      // Require admin token or development environment
      const adminToken = req.headers["x-admin-token"];
      const isDevelopment = process.env.NODE_ENV === "development";

      if (!isDevelopment) {
        if (
          !process.env.ADMIN_TOKEN ||
          adminToken !== process.env.ADMIN_TOKEN
        ) {
          return res.status(403).json({
            message: "Forbidden: Admin access required",
            timestamp: new Date().toISOString(),
          });
        }
      }

      const cache = getCacheServiceInstance();
      await cache.clear();
      cache.resetMetrics();

      res.json({
        message: "Cache cleared successfully",
        timestamp: new Date().toISOString(),
      });
    } catch (error) {
      res.status(500).json({
        message: "Cache service not available",
        error: error instanceof Error ? error.message : "Unknown error",
        timestamp: new Date().toISOString(),
      });
    }
  });

  // DIAGNOSTIC ENDPOINTS - Systematic API debugging

  // Claude AI Health Check
  app.get("/api/health/claude", async (req, res) => {
    try {
      console.log("üîß DIAGNOSTIC: Testing Claude AI connection...");
      const isConnected = await claudeService.testConnection();

      if (isConnected) {
        console.log("‚úÖ DIAGNOSTIC: Claude AI connection successful");
        res.json({
          success: true,
          message: "Claude AI connection successful",
          timestamp: new Date().toISOString(),
        });
      } else {
        console.log("‚ùå DIAGNOSTIC: Claude AI connection failed");
        res.status(503).json({
          success: false,
          message: "Claude AI connection failed",
          timestamp: new Date().toISOString(),
        });
      }
    } catch (error) {
      console.error("üö® DIAGNOSTIC: Claude AI health check error:", error);
      res.status(503).json({
        success: false,
        message: "Claude AI connection error",
        error: error instanceof Error ? error.message : "Unknown error",
        timestamp: new Date().toISOString(),
      });
    }
  });

  // Removed city smoke test - not needed with Claude-only estimates

  // Step 3: Region Mapping Validation
  app.get("/api/health/region-mapping", (req, res) => {
    const region = (req.query.region as string) || "asia";

    // Use actual region mapping instead of inline duplication
    const regionCountries =
      getCountriesForRegion(region.toLowerCase() as any) || [];

    console.log(
      `üó∫Ô∏è  DIAGNOSTIC: Region '${region}' mapped to countries: ${regionCountries.join(", ")}`,
    );

    res.json({
      success: regionCountries.length > 0,
      message: `Region '${region}' mapped to ${regionCountries.length} countries`,
      region,
      countries: regionCountries,
      timestamp: new Date().toISOString(),
    });
  });

  // Step 4: Claude AI Health Check (Protected)
  app.get("/api/health/claude", async (req, res) => {
    try {
      // SECURITY: Require admin access or development environment to prevent cost abuse
      const adminToken = req.headers["x-admin-token"];
      const isDevelopment = process.env.NODE_ENV === "development";

      if (!isDevelopment) {
        if (
          !process.env.ADMIN_TOKEN ||
          adminToken !== process.env.ADMIN_TOKEN
        ) {
          return res.status(403).json({
            message:
              "Forbidden: Admin access required for health checks that incur costs",
            timestamp: new Date().toISOString(),
          });
        }
      }

      console.log("üîß DIAGNOSTIC: Testing Claude AI integration...");
      if (!claudeService) {
        return res.status(503).json({
          success: false,
          message: "Claude service not configured - check ANTHROPIC_API_KEY",
          timestamp: new Date().toISOString(),
        });
      }

      const startTime = Date.now();

      // Test hotel pricing with individual error handling
      let hotelService: {
        available: boolean;
        error: string;
        testResult: any;
        durationMs: number;
      } = { available: false, error: "", testResult: null, durationMs: 0 };
      try {
        const hotelStart = Date.now();
        console.log("üì° DIAGNOSTIC: Testing Claude hotel pricing for Tokyo...");
        const hotelTest = await claudeService.getHotelPricing(
          "Tokyo",
          "Japan",
          new Date().getMonth() + 1,
        );
        hotelService = {
          available: true,
          error: "",
          testResult: {
            p25: hotelTest.p25Usd,
            p50: hotelTest.p50Usd,
            p75: hotelTest.p75Usd,
            confidence: hotelTest.confidence,
          },
          durationMs: Date.now() - hotelStart,
        };
      } catch (error) {
        hotelService.error =
          error instanceof Error ? error.message : "Unknown error";
        hotelService.durationMs = Date.now() - startTime;
      }

      // Test daily costs with individual error handling
      let dailyCostService: {
        available: boolean;
        error: string;
        testResult: any;
        durationMs: number;
      } = { available: false, error: "", testResult: null, durationMs: 0 };
      try {
        const dailyStart = Date.now();
        console.log("üì° DIAGNOSTIC: Testing Claude daily costs for Tokyo...");
        const dailyTest = await claudeService.getDailyCosts("Tokyo", "Japan");
        dailyCostService = {
          available: true,
          error: "",
          testResult: {
            food: dailyTest.dailyFoodUsd,
            transport: dailyTest.dailyTransportUsd,
            misc: dailyTest.dailyMiscUsd,
          },
          durationMs: Date.now() - dailyStart,
        };
      } catch (error) {
        dailyCostService.error =
          error instanceof Error ? error.message : "Unknown error";
        dailyCostService.durationMs = Date.now() - startTime;
      }

      // Get cache metrics
      const cache = getCacheServiceInstance();
      const cacheMetrics = cache.getMetrics();

      const overallSuccess =
        hotelService.available && dailyCostService.available;
      console.log(
        `${overallSuccess ? "‚úÖ" : "‚ö†Ô∏è"} DIAGNOSTIC: Claude AI integration ${overallSuccess ? "successful" : "partial"}`,
      );

      res.json({
        success: overallSuccess,
        message: overallSuccess
          ? "Claude AI integration successful"
          : "Claude AI integration partially available",
        services: {
          hotelPricing: hotelService,
          dailyCosts: dailyCostService,
        },
        caching: {
          enabled: true,
          metrics: {
            hits: cacheMetrics.hits,
            misses: cacheMetrics.misses,
            hitRate:
              cacheMetrics.hits / (cacheMetrics.hits + cacheMetrics.misses) ||
              0,
          },
        },
        totalDurationMs: Date.now() - startTime,
        timestamp: new Date().toISOString(),
      });
    } catch (error) {
      console.error("üö® DIAGNOSTIC: Claude health check error:", error);
      res.status(503).json({
        success: false,
        message: "Claude AI integration failed",
        error: error instanceof Error ? error.message : "Unknown error",
        timestamp: new Date().toISOString(),
      });
    }
  });

  // Admin route to regenerate daily costs with Claude
  app.post("/api/admin/regenerate-daily-costs", async (req, res) => {
    try {
      console.log("Admin: Regenerating daily costs with Claude API...");

      // Check admin access
      const adminToken = req.headers["x-admin-token"];
      const isDevelopment = process.env.NODE_ENV === "development";

      if (!isDevelopment) {
        if (
          !process.env.ADMIN_TOKEN ||
          adminToken !== process.env.ADMIN_TOKEN
        ) {
          return res.status(403).json({
            message: "Forbidden: Admin access required",
            timestamp: new Date().toISOString(),
          });
        }
      }

      // Get all cities
      const cities = await storage.getCities();
      if (cities.length === 0) {
        return res.status(400).json({
          success: false,
          message: "No cities found. Initialize mock data first.",
          timestamp: new Date().toISOString(),
        });
      }

      // Clear existing daily costs
      console.log("Clearing existing daily costs...");
      const dailyCosts = await storage.getAllDailyCosts();
      for (const cost of dailyCosts) {
        await storage.deleteDailyCosts(cost.id);
      }
      console.log(`Cleared ${dailyCosts.length} existing daily cost records`);

      // Generate new ones with Claude
      await travelApiService.generateDailyCostsWithClaude(cities);

      res.json({
        success: true,
        message: `Successfully regenerated daily costs for ${cities.length} cities using Claude API`,
        timestamp: new Date().toISOString(),
      });
    } catch (error) {
      console.error("Failed to regenerate daily costs:", error);
      res.status(500).json({
        success: false,
        message: error instanceof Error ? error.message : "Unknown error",
        timestamp: new Date().toISOString(),
      });
    }
  });

  // Removed Amadeus test and update endpoints - not needed with Claude-only estimates

  // Development endpoint to clear PHX flight cache for distance-based pricing
  app.post("/api/dev/clear-phx-flight-cache", async (req, res) => {
    try {
      if (process.env.NODE_ENV !== "development") {
        return res.status(403).json({
          success: false,
          message: "Development endpoint only",
          timestamp: new Date().toISOString()
        });
      }

      // Clear PHX flight cache entries
      const clearedKeys = await claudeService.clearFlightCache("PHX");

      res.json({
        success: true,
        message: `Cleared ${clearedKeys} PHX flight cache entries. Next searches will use distance-based pricing.`,
        timestamp: new Date().toISOString()
      });
    } catch (error) {
      console.error("Failed to clear PHX flight cache:", error);
      res.status(500).json({
        success: false,
        message: error instanceof Error ? error.message : "Unknown error",
        timestamp: new Date().toISOString()
      });
    }
  });

  // Development endpoint to clear travel search cache to force distance calculations
  app.post("/api/dev/clear-travel-search-cache", async (req, res) => {
    try {
      if (process.env.NODE_ENV !== "development") {
        return res.status(403).json({
          success: false,
          message: "Development endpoint only",
          timestamp: new Date().toISOString()
        });
      }

      // Clear travel search cache to force new distance-based calculations
      const cache = getCacheServiceInstance();
      const clearedCount = await cache.clearAll();

      console.log(`üóëÔ∏è Cleared ${clearedCount} travel search cache entries. Next searches will use distance-based flight calculations.`);

      res.json({
        success: true,
        message: `Cleared ${clearedCount} travel search cache entries. Next searches will use distance-based flight calculations.`,
        clearedCount,
        timestamp: new Date().toISOString()
      });
    } catch (error) {
      console.error("Failed to clear travel search cache:", error);
      res.status(500).json({
        success: false,
        message: error instanceof Error ? error.message : "Unknown error",
        timestamp: new Date().toISOString()
      });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  cities, 
  flightAverages, 
  hotelStats, 
  dailyCosts, 
  cacheMetadata,
  type City, 
  type InsertCity,
  type FlightAverage,
  type InsertFlightAverage,
  type HotelStats,
  type InsertHotelStats,
  type DailyCosts,
  type InsertDailyCosts,
  type CacheMetadata,
  type TravelSearchParams,
  type CityRecommendation
} from "@shared/schema";
import { db } from "./db";
import { eq, and, inArray, isNull, or, sql, desc } from "drizzle-orm";

export interface IStorage {
  // Cities
  getCities(): Promise<City[]>;
  getCitiesByRegion(region: string): Promise<City[]>;
  getCitiesByCountry(country: string): Promise<City[]>;
  createCity(city: InsertCity): Promise<City>;

  // Flight averages
  getFlightAverage(originIata: string, cityIata: string, month?: number): Promise<FlightAverage | undefined>;
  createFlightAverage(flight: InsertFlightAverage): Promise<FlightAverage>;

  // Hotel stats
  getHotelStats(cityId: string, month?: number): Promise<HotelStats | undefined>;
  createHotelStats(stats: InsertHotelStats): Promise<HotelStats>;

  // Daily costs
  getDailyCosts(cityId: string): Promise<DailyCosts | undefined>;
  createDailyCosts(costs: InsertDailyCosts): Promise<DailyCosts>;

  // Travel recommendations
  getTravelRecommendations(params: TravelSearchParams): Promise<CityRecommendation[]>;

  // Cache metadata
  getCacheMetadata(cacheKey: string): Promise<CacheMetadata | undefined>;
  setCacheMetadata(cacheKey: string, dataType: string, expiresAt: Date, metadata?: any): Promise<CacheMetadata>;
}

export class DatabaseStorage implements IStorage {
  async getCities(): Promise<City[]> {
    return await db.select().from(cities).where(eq(cities.enabled, 1));
  }

  async getCitiesByRegion(region: string): Promise<City[]> {
    return await db.select().from(cities)
      .where(and(eq(cities.region, region), eq(cities.enabled, 1)));
  }

  async getCitiesByCountry(country: string): Promise<City[]> {
    return await db.select().from(cities)
      .where(and(eq(cities.country, country), eq(cities.enabled, 1)));
  }

  async createCity(insertCity: InsertCity): Promise<City> {
    const [city] = await db.insert(cities).values(insertCity).returning();
    return city;
  }

  async getFlightAverage(originIata: string, cityIata: string, month?: number): Promise<FlightAverage | undefined> {
    // First try to get specific month data if month is provided
    if (month !== undefined) {
      const [specificResult] = await db.select().from(flightAverages)
        .where(and(
          eq(flightAverages.originIata, originIata),
          eq(flightAverages.cityIata, cityIata),
          eq(flightAverages.month, month)
        ));

      if (specificResult) {
        return specificResult;
      }
    }

    // Fall back to annual average (month = null)
    const [fallbackResult] = await db.select().from(flightAverages)
      .where(and(
        eq(flightAverages.originIata, originIata),
        eq(flightAverages.cityIata, cityIata),
        isNull(flightAverages.month)
      ));

    return fallbackResult || undefined;
  }

  async createFlightAverage(flight: InsertFlightAverage): Promise<FlightAverage> {
    const [result] = await db.insert(flightAverages).values(flight).returning();
    return result;
  }

  async getHotelStats(cityId: string, month?: number): Promise<HotelStats | undefined> {
    // First try to get specific month data if month is provided
    if (month !== undefined) {
      const [specificResult] = await db.select().from(hotelStats)
        .where(and(eq(hotelStats.cityId, cityId), eq(hotelStats.month, month)));

      if (specificResult) {
        return specificResult;
      }
    }

    // Fall back to annual average (month = null)
    const [fallbackResult] = await db.select().from(hotelStats)
      .where(and(eq(hotelStats.cityId, cityId), isNull(hotelStats.month)));

    return fallbackResult || undefined;
  }

  async createHotelStats(stats: InsertHotelStats): Promise<HotelStats> {
    const [result] = await db.insert(hotelStats).values(stats).returning();
    return result;
  }

  async getDailyCosts(cityId: string): Promise<DailyCosts | undefined> {
    const [result] = await db.select().from(dailyCosts)
      .where(eq(dailyCosts.cityId, cityId));

    return result || undefined;
  }

  async getAllDailyCosts(): Promise<DailyCosts[]> {
    return await db.select().from(dailyCosts);
  }

  async deleteDailyCosts(id: string): Promise<void> {
    await db.delete(dailyCosts).where(eq(dailyCosts.id, id));
  }

  async createDailyCosts(costs: InsertDailyCosts): Promise<DailyCosts> {
    const [result] = await db.insert(dailyCosts).values(costs).returning();
    return result;
  }

  async getTravelRecommendations(params: TravelSearchParams): Promise<CityRecommendation[]> {
    let whereConditions = [eq(cities.enabled, 1)];

    if (params.region) {
      whereConditions.push(eq(cities.region, params.region));
    }

    if (params.country) {
      whereConditions.push(eq(cities.country, params.country));
    }

    const targetCities = await db.select().from(cities).where(and(...whereConditions));

    const recommendations: CityRecommendation[] = [];

    for (const city of targetCities) {
      // Get flight data
      const flightData = params.origin 
        ? await this.getFlightAverage(params.origin, city.iataCityCode || city.name, params.month)
        : null;

      // Skip if no flight data when origin is specified and includeEstimates is false
      if (params.origin && !flightData && !params.includeEstimates) continue;

      // Get hotel data
      const hotelData = await this.getHotelStats(city.id, params.month);
      if (!hotelData) continue;

      // Get daily costs
      const dailyData = await this.getDailyCosts(city.id);
      if (!dailyData) continue;

      const flightCost = flightData ? parseFloat(flightData.avgRoundtripUsd) : 0;
      const hotelP25 = parseFloat(hotelData.p25Usd);
      const hotelP50 = parseFloat(hotelData.p50Usd);
      const hotelP75 = parseFloat(hotelData.p75Usd);
      const dailyCost = parseFloat(dailyData.dailyFoodUsd) + 
                       parseFloat(dailyData.dailyTransportUsd) + 
                       parseFloat(dailyData.dailyMiscUsd);

      const totalP25 = flightCost + (hotelP25 * params.nights) + (dailyCost * params.nights);
      const totalP50 = flightCost + (hotelP50 * params.nights) + (dailyCost * params.nights);
      const totalP75 = flightCost + (hotelP75 * params.nights) + (dailyCost * params.nights);

      // Filter by budget - exclude if p75 exceeds budget by more than 15%
      if (totalP75 > params.budget * 1.15) continue;

      const confidence = this.calculateConfidence(flightData || null, hotelData, dailyData);

      recommendations.push({
        cityId: city.id,
        city: city.name,
        country: city.country,
        region: city.region,
        nights: params.nights,
        totals: {
          p25: Math.round(totalP25),
          p35: Math.round(totalP25 * 1.05), // Approximate p35 for storage fallback
          p50: Math.round(totalP50),
          p75: Math.round(totalP75)
        },
        breakdown: {
          flight: Math.round(flightCost),
          hotelPerNightP25: Math.round(hotelP25),
          hotelPerNightP35: Math.round(hotelP25 * 1.1), // Approximate p35 for storage fallback  
          hotelPerNightP50: Math.round(hotelP50),
          hotelPerNightP75: Math.round(hotelP75),
          dailyPerDay: Math.round(dailyCost)
        },
        rangeNote: "Hotels vary by season ¬±15‚Äì25%; flights ¬±20%",
        confidence: confidence,
        lastUpdatedISO: (hotelData.updatedAt || new Date()).toISOString()
      });
    }

    // Apply sorting based on user preference
    if (params.sort === 'price-low-high') {
      // Optimized: precompute country averages once before sorting
      const countryAverages = new Map<string, number>();
      const countryGroups = new Map<string, typeof recommendations>();

      // Group cities by country
      recommendations.forEach(rec => {
        if (!countryGroups.has(rec.country)) {
          countryGroups.set(rec.country, []);
        }
        countryGroups.get(rec.country)!.push(rec);
      });

      // Calculate average price per country
      countryGroups.forEach((cities, country) => {
        const avgPrice = cities.reduce((sum, city) => sum + city.totals.p50, 0) / cities.length;
        countryAverages.set(country, avgPrice);
      });

      // Sort by country average first, then by city price within country
      recommendations.sort((a, b) => {
        const countryCompare = (countryAverages.get(a.country) || 0) - (countryAverages.get(b.country) || 0);
        return countryCompare !== 0 ? countryCompare : a.totals.p50 - b.totals.p50;
      });
    } else {
      recommendations.sort((a, b) => {
        switch (params.sort) {
          case 'alphabetical':
            return a.city.localeCompare(b.city);
          case 'confidence':
            const confidenceOrder = { 'high': 3, 'medium': 2, 'low': 1 };
            return confidenceOrder[b.confidence] - confidenceOrder[a.confidence];
          case 'region':
            const regionCompare = a.region.localeCompare(b.region);
            return regionCompare !== 0 ? regionCompare : a.city.localeCompare(b.city);
          default:
            return a.city.localeCompare(b.city);
        }
      });
    }

    // Return ALL sorted results (pagination handled at service layer)
    return recommendations;
  }

  private calculateConfidence(
    flightData: FlightAverage | null, 
    hotelData: HotelStats, 
    dailyData: DailyCosts
  ): 'high' | 'medium' | 'low' {
    const flightConfidence = flightData?.confidence || 'low';
    const hotelConfidence = hotelData.confidence;

    if (flightConfidence === 'high' && hotelConfidence === 'high') return 'high';
    if (flightConfidence === 'low' || hotelConfidence === 'low') return 'low';
    return 'medium';
  }

  async getCacheMetadata(cacheKey: string): Promise<CacheMetadata | undefined> {
    const [result] = await db.select().from(cacheMetadata)
      .where(eq(cacheMetadata.cacheKey, cacheKey));

    return result || undefined;
  }

  async setCacheMetadata(cacheKey: string, dataType: string, expiresAt: Date, metadata?: any): Promise<CacheMetadata> {
    const [result] = await db.insert(cacheMetadata)
      .values({
        cacheKey,
        dataType,
        expiresAt,
        metadata
      })
      .onConflictDoUpdate({
        target: cacheMetadata.cacheKey,
        set: {
          dataType,
          expiresAt,
          metadata,
          lastUpdated: sql`CURRENT_TIMESTAMP`
        }
      })
      .returning();

    return result;
  }
}

export const storage = new DatabaseStorage();

