server/services/rate-limiter.ts.content:
// Rate limiter for API calls to prevent overloading Amadeus/Claude services
export interface RateLimiterConfig {
  maxConcurrent: number;
  minDelayMs: number;
  maxRetries: number;
  circuitBreakerThreshold: number;
  circuitBreakerTimeoutMs: number;
}

interface QueuedTask<T> {
  fn: () => Promise<T>;
  resolve: (value: T) => void;
  reject: (error: Error) => void;
  retries: number;
}

export class RateLimiter {
  private queue: QueuedTask<any>[] = [];
  private running = 0;
  private consecutive429s = 0;
  private circuitOpen = false;
  private circuitOpenSince?: Date;

  constructor(private config: RateLimiterConfig) {}

  /**
   * Schedule a function to run with rate limiting
   */
  async schedule<T>(fn: () => Promise<T>): Promise<T> {
    return new Promise((resolve, reject) => {
      this.queue.push({
        fn,
        resolve,
        reject,
        retries: 0
      });

      this.processQueue();
    });
  }

  /**
   * Process the queue respecting rate limits
   */
  private async processQueue(): Promise<void> {
    // Check if circuit breaker is open
    if (this.circuitOpen) {
      if (this.circuitOpenSince && 
          Date.now() - this.circuitOpenSince.getTime() > this.config.circuitBreakerTimeoutMs) {
        console.log(`üîÑ Circuit breaker reset after ${this.config.circuitBreakerTimeoutMs}ms`);
        this.circuitOpen = false;
        this.consecutive429s = 0;
      } else {
        return; // Circuit still open
      }
    }

    // Don't exceed max concurrent requests
    if (this.running >= this.config.maxConcurrent || this.queue.length === 0) {
      return;
    }

    const task = this.queue.shift();
    if (!task) return;

    this.running++;

    try {
      // Add minimum delay between requests
      if (this.config.minDelayMs > 0) {
        await this.sleep(this.config.minDelayMs + this.jitter(50));
      }

      const result = await task.fn();
      task.resolve(result);
      this.consecutive429s = 0; // Reset on success

    } catch (error: any) {
      // Handle rate limit (429) errors
      if (error.status === 429 || error.message?.includes('429')) {
        this.consecutive429s++;
        console.warn(`‚ö†Ô∏è Rate limit hit (${this.consecutive429s}/${this.config.circuitBreakerThreshold})`);

        // Circuit breaker: too many consecutive 429s
        if (this.consecutive429s >= this.config.circuitBreakerThreshold) {
          this.openCircuitBreaker();
          task.reject(new Error(`Circuit breaker opened after ${this.consecutive429s} consecutive 429 errors`));
        } else if (task.retries < this.config.maxRetries) {
          // Retry with exponential backoff
          const backoffMs = Math.min(2000 * Math.pow(2, task.retries), 30000);
          console.log(`üîÑ Retrying in ${backoffMs}ms (attempt ${task.retries + 1}/${this.config.maxRetries})`);

          setTimeout(() => {
            task.retries++;
            this.queue.unshift(task); // Add back to front of queue
            this.processQueue();
          }, backoffMs + this.jitter(1000));
        } else {
          task.reject(new Error(`Max retries exceeded after ${task.retries} attempts`));
        }
      } else {
        // Non-429 error, don't retry
        task.reject(error);
      }
    } finally {
      this.running--;
      // Continue processing queue
      setTimeout(() => this.processQueue(), 0);
    }
  }

  /**
   * Open circuit breaker to pause all requests
   */
  private openCircuitBreaker(): void {
    this.circuitOpen = true;
    this.circuitOpenSince = new Date();
    console.error(`üö® Circuit breaker OPENED - pausing all requests for ${this.config.circuitBreakerTimeoutMs}ms`);
  }

  /**
   * Add random jitter to prevent thundering herd
   */
  private jitter(maxMs: number): number {
    return Math.random() * maxMs;
  }

  /**
   * Sleep utility
   */
  private sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Get current status for debugging
   */
  getStatus(): {
    running: number;
    queued: number;
    consecutive429s: number;
    circuitOpen: boolean;
  } {
    return {
      running: this.running,
      queued: this.queue.length,
      consecutive429s: this.consecutive429s,
      circuitOpen: this.circuitOpen,
    };
  }
}

// Global rate limiter instances
const amadeusTravelRateLimiter = new RateLimiter({
  maxConcurrent: 2, // Balanced: 2 concurrent to allow progress while preventing spam
  minDelayMs: 750,  // Reduced to 750ms for better throughput while staying conservative 
  maxRetries: 3,    // Reduced retries to fail faster
  circuitBreakerThreshold: 2, // Open circuit after just 2 consecutive 429s
  circuitBreakerTimeoutMs: 90000, // 1.5 minute pause when circuit opens
});

const claudeRateLimiter = new RateLimiter({
  maxConcurrent: 4,
  minDelayMs: 200,
  maxRetries: 3,
  circuitBreakerThreshold: 3,
  circuitBreakerTimeoutMs: 30000, // 30 seconds pause
});

export { amadeusTravelRateLimiter, claudeRateLimiter };

client/src/components/airport-autocomple.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Check, Plane, MapPin } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";

interface AirportResult {
  iataCode: string;
  name: string;
  cityName: string;
  countryName: string;
  countryCode: string;
  type: "airport" | "city";
  displayText: string;
  coordinates: {
    latitude: number;
    longitude: number;
  };
}

interface AirportSearchResponse {
  success: boolean;
  query: string;
  results: AirportResult[];
  count: number;
  timestamp: string;
}

interface AirportAutocompleteProps {
  value?: string;
  onValueChange: (value: string) => void;
  placeholder?: string;
  disabled?: boolean;
}

export function AirportAutocomplete({ 
  value = "", 
  onValueChange, 
  placeholder = "Search airports...", 
  disabled 
}: AirportAutocompleteProps) {
  const [open, setOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedValue, setSelectedValue] = useState(value);

  // Debounce search query
  const [debouncedQuery, setDebouncedQuery] = useState("");
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedQuery(searchQuery);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery]);

  // Fetch airport search results
  const { data, isLoading, error } = useQuery<AirportSearchResponse>({
    queryKey: ['airports-search', debouncedQuery],
    queryFn: async () => {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 30000); // 30s timeout

      try {
        const url = `/api/airports/search?q=${encodeURIComponent(debouncedQuery)}`;
        const res = await fetch(url, {
          credentials: "include",
          signal: controller.signal,
        });

        clearTimeout(timeoutId);

        if (!res.ok) {
          const text = (await res.text()) || res.statusText;
          throw new Error(`${res.status}: ${text}`);
        }

        return await res.json();
      } catch (error) {
        clearTimeout(timeoutId);
        throw error;
      }
    },
    enabled: debouncedQuery.length >= 2, // Only search if 2+ characters
    staleTime: 5 * 60 * 1000, // Cache for 5 minutes
    gcTime: 10 * 60 * 1000, // Keep in cache for 10 minutes
    retry: (failureCount, error) => {
      // Retry up to 3 times for network errors, but not for 4xx/5xx errors
      if (failureCount >= 3) return false;
      return error?.message?.includes('abort') || error?.message?.includes('timeout') || error?.message?.includes('network');
    },
    retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 5000), // Exponential backoff
    refetchOnWindowFocus: false, // Don't refetch when window regains focus
    networkMode: 'online', // Only run when online
  });

  // Update selected value when external value changes
  useEffect(() => {
    setSelectedValue(value);
  }, [value]);

  // Find selected airport details for display
  const selectedAirport = data?.results?.find(airport => airport.iataCode === selectedValue);
  const displayValue = selectedAirport 
    ? `${selectedAirport.iataCode} - ${selectedAirport.name}, ${selectedAirport.cityName}`
    : selectedValue;

  const handleSelect = (airport: AirportResult) => {
    setSelectedValue(airport.iataCode);
    onValueChange(airport.iataCode);
    setOpen(false);
    setSearchQuery("");
  };

  const handleClear = () => {
    setSelectedValue("");
    onValueChange("");
    setSearchQuery("");
  };

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          className="w-full justify-between font-normal"
          disabled={disabled}
          data-testid="button-airport-autocomplete"
        >
          <span className={cn("truncate", !selectedValue && "text-muted-foreground")}>
            {selectedValue ? displayValue : placeholder}
          </span>
          <Plane className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-[400px] p-0" align="start">
        <Command shouldFilter={false}>
          <CommandInput
            placeholder="Type to search airports..."
            value={searchQuery}
            onValueChange={setSearchQuery}
            data-testid="input-airport-search"
          />
          <CommandList>
            {/* Loading State */}
            {isLoading && debouncedQuery.length >= 2 && (
              <div className="py-6 text-center text-sm text-muted-foreground" data-testid="loading-airports">
                Searching airports...
              </div>
            )}

            {/* Error State */}
            {(error || (data && !data.success)) && (
              <div className="py-6 text-center text-sm text-destructive" data-testid="error-airports">
                {error?.message?.includes('abort') || error?.message?.includes('timeout') ? 
                  'Search timed out. Please try again.' : 
                  'Failed to search airports. Please try again.'
                }
                {import.meta.env.DEV && error && (
                  <div className="mt-2 text-xs opacity-75">
                    Debug: {error.message}
                  </div>
                )}
              </div>
            )}

            {/* No Results */}
            {!isLoading && !error && data && data.results.length === 0 && debouncedQuery.length >= 2 && (
              <CommandEmpty data-testid="empty-airports">
                No airports found for "{debouncedQuery}".
              </CommandEmpty>
            )}

            {/* Search Prompt */}
            {!isLoading && !error && debouncedQuery.length < 2 && (
              <div className="py-6 text-center text-sm text-muted-foreground" data-testid="prompt-airports">
                Type at least 2 characters to search airports
              </div>
            )}

            {/* Clear Selection */}
            {selectedValue && (
              <CommandGroup heading="Current Selection">
                <CommandItem
                  onSelect={handleClear}
                  className="text-destructive"
                  data-testid="button-clear-airport"
                >
                  Clear selection
                </CommandItem>
              </CommandGroup>
            )}

            {/* Results */}
            {data && data.results.length > 0 && (
              <CommandGroup heading={`${data.count} airports found`}>
                {data.results.map((airport) => (
                  <CommandItem
                    key={`${airport.iataCode}-${airport.type}`}
                    onSelect={() => handleSelect(airport)}
                    className="flex items-center justify-between"
                    data-testid={`option-airport-${airport.iataCode}`}
                  >
                    <div className="flex items-center">
                      {airport.type === "airport" ? (
                        <Plane className="mr-2 h-4 w-4 opacity-50" />
                      ) : (
                        <MapPin className="mr-2 h-4 w-4 opacity-50" />
                      )}
                      <div className="flex flex-col">
                        <span className="font-medium">
                          {airport.iataCode} - {airport.name}
                        </span>
                        <span className="text-sm text-muted-foreground">
                          {airport.cityName}, {airport.countryName}
                        </span>
                      </div>
                    </div>
                    <Check
                      className={cn(
                        "ml-2 h-4 w-4",
                        selectedValue === airport.iataCode ? "opacity-100" : "opacity-0"
                      )}
                    />
                  </CommandItem>
                ))}
              </CommandGroup>
            )}
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

server/services/flight-anchor-cache-serv.content:
import type { CacheConfig, CacheMetrics } from "./cache-service";
import { Redis } from '@upstash/redis';

// Flight cache data structure
export interface FlightAnchorData {
  origin: string;
  destination: string;
  month?: number;
  nights: number;
  prices: number[];
  currency: string;
  confidence: 'high' | 'medium' | 'low';
  averagePrice: number;
  lastUpdated: string;
  samplesCount: number;
}

// Flight cache entry with TTL metadata
interface FlightCacheEntry {
  data: FlightAnchorData;
  timestamp: number;
  expiresAt: number;
  confidence: 'high' | 'medium' | 'low';
}

export class FlightAnchorCacheService {
  private redisClient: Redis | null = null;
  private memoryStore: Map<string, FlightCacheEntry>;
  private config: CacheConfig;
  private metrics: CacheMetrics;
  private isRedisConnected = false;
  private keyPrefix = 'flight_anchor:';
  private cleanupInterval: NodeJS.Timeout;

  // Geographic clustering for broader cache coverage
  private readonly airportClusters = {
    // NYC Area - multiple airports serve same metropolitan area
    'NYC': ['JFK', 'LGA', 'EWR'],
    // London Area
    'LON': ['LHR', 'LGW', 'STN', 'LTN'], 
    // Paris Area
    'PAR': ['CDG', 'ORY'],
    // Tokyo Area
    'TYO': ['NRT', 'HND'],
    // San Francisco Bay Area
    'SF': ['SFO', 'OAK', 'SJC'],
    // Los Angeles Area
    'LA': ['LAX', 'BUR', 'LGB', 'SNA'],
    // Washington DC Area
    'WAS': ['DCA', 'IAD', 'BWI'],
    // Chicago Area
    'CHI': ['ORD', 'MDW'],
    // Miami Area  
    'MIA': ['MIA', 'FLL', 'PBI']
  };

  // TTL configuration based on confidence levels
  private readonly ttlConfig = {
    high: 7 * 24 * 60 * 60 * 1000,   // 7 days for high confidence data
    medium: 3 * 24 * 60 * 60 * 1000, // 3 days for medium confidence
    low: 1 * 24 * 60 * 60 * 1000     // 1 day for low confidence
  };

  constructor(config: Partial<CacheConfig> = {}) {
    this.config = {
      ttl: config.ttl || this.ttlConfig.medium, // Default to medium confidence TTL
      maxSize: config.maxSize || 5000, // Higher limit for flight anchor cache
      checkPeriod: config.checkPeriod || 60 * 1000
    };

    this.memoryStore = new Map();
    this.metrics = {
      hits: 0,
      misses: 0,
      totalRequests: 0,
      hitRate: 0
    };

    // Set up periodic cleanup
    this.cleanupInterval = setInterval(() => {
      this.cleanup();
    }, this.config.checkPeriod);

    this.initializeRedis();
  }

  private async initializeRedis(): Promise<void> {
    if (process.env.DISABLE_REDIS === 'true') {
      console.log('Redis disabled for flight anchor cache');
      return;
    }

    try {
      let upstashUrl = process.env.UPSTASH_REDIS_REST_URL;
      let upstashToken = process.env.UPSTASH_REDIS_REST_TOKEN;

      if (upstashUrl && upstashToken) {
        // Remove quotes if present
        upstashUrl = upstashUrl.replace(/^["'](.*)["']$/, '$1');
        upstashToken = upstashToken.replace(/^["'](.*)["']$/, '$1');

        this.redisClient = new Redis({
          url: upstashUrl,
          token: upstashToken
        });

        await this.redisClient.ping();
        console.log('Flight anchor cache Redis connected successfully');
        this.isRedisConnected = true;
      }
    } catch (error) {
      console.log('Flight anchor cache using memory fallback:', error instanceof Error ? error.message : 'Unknown error');
      this.isRedisConnected = false;
    }
  }

  /**
   * Generate cache key for flight route with geographic clustering
   */
  private generateCacheKey(origin: string, destination: string, month?: number, nights: number = 7): string {
    // Apply geographic clustering to use broader cache coverage
    const clusterOrigin = this.getClusterKey(origin) || origin;
    const clusterDestination = this.getClusterKey(destination) || destination;

    const monthKey = month ? month.toString() : 'any';
    return `${this.keyPrefix}${clusterOrigin}:${clusterDestination}:${monthKey}:${nights}`;
  }

  /**
   * Get cluster key for airport (e.g., JFK -> NYC)
   */
  private getClusterKey(airportCode: string): string | null {
    for (const [cluster, airports] of Object.entries(this.airportClusters)) {
      if (airports.includes(airportCode)) {
        return cluster;
      }
    }
    return null;
  }

  /**
   * Get TTL based on confidence level
   */
  private getTTL(confidence: 'high' | 'medium' | 'low'): number {
    return this.ttlConfig[confidence];
  }

  /**
   * Get flight anchor data from cache
   */
  async get(origin: string, destination: string, month?: number, nights: number = 7): Promise<FlightAnchorData | null> {
    const key = this.generateCacheKey(origin, destination, month, nights);
    this.metrics.totalRequests++;

    // Try Redis first
    if (this.isRedisConnected && this.redisClient) {
      try {
        const cached = await this.redisClient.get<FlightAnchorData>(key);
        if (cached) {
          this.metrics.hits++;
          console.log(`Flight anchor cache HIT (Redis): ${origin} ‚Üí ${destination}`);
          this.updateHitRate();
          return cached;
        }
      } catch (error) {
        console.warn('Redis get error for flight anchor:', error);
      }
    }

    // Fallback to memory cache
    const memoryEntry = this.memoryStore.get(key);
    if (memoryEntry) {
      const now = Date.now();
      if (now <= memoryEntry.expiresAt) {
        // Update timestamp for true LRU behavior
        memoryEntry.timestamp = now;
        this.memoryStore.set(key, memoryEntry);

        this.metrics.hits++;
        console.log(`Flight anchor cache HIT (memory): ${origin} ‚Üí ${destination}`);
        this.updateHitRate();
        return memoryEntry.data;
      } else {
        // Expired entry
        this.memoryStore.delete(key);
      }
    }

    this.metrics.misses++;
    console.log(`Flight anchor cache MISS: ${origin} ‚Üí ${destination}`);
    this.updateHitRate();
    return null;
  }

  /**
   * Set flight anchor data in cache
   */
  async set(data: FlightAnchorData): Promise<void> {
    const key = this.generateCacheKey(data.origin, data.destination, data.month, data.nights);
    const ttl = this.getTTL(data.confidence);
    const expiresAt = Date.now() + ttl;

    // Store in Redis
    if (this.isRedisConnected && this.redisClient) {
      try {
        await this.redisClient.set(key, data, { ex: Math.floor(ttl / 1000) });
        console.log(`Flight anchor cache SET (Redis): ${data.origin} ‚Üí ${data.destination} [${data.confidence}] TTL=${Math.floor(ttl/3600000)}h`);
      } catch (error) {
        console.warn('Redis set error for flight anchor:', error);
      }
    }

    // Store in memory cache as fallback
    const entry: FlightCacheEntry = {
      data,
      timestamp: Date.now(),
      expiresAt,
      confidence: data.confidence
    };

    // Check memory capacity
    if (this.memoryStore.size >= this.config.maxSize) {
      this.evictLRU();
    }

    this.memoryStore.set(key, entry);
    console.log(`Flight anchor cache SET (memory): ${data.origin} ‚Üí ${data.destination} [${data.confidence}]`);
  }

  /**
   * Check if cached data is still fresh enough to use
   */
  isDataFresh(data: FlightAnchorData, maxAge: number = 24 * 60 * 60 * 1000): boolean {
    const dataAge = Date.now() - new Date(data.lastUpdated).getTime();
    return dataAge <= maxAge;
  }

  /**
   * Get cache statistics
   */
  getMetrics(): CacheMetrics & { cacheSize: number; redisConnected: boolean } {
    return {
      ...this.metrics,
      cacheSize: this.memoryStore.size,
      redisConnected: this.isRedisConnected
    };
  }

  /**
   * Clean up expired memory cache entries
   */
  private cleanup(): void {
    const now = Date.now();
    let removedCount = 0;

    const entries = Array.from(this.memoryStore.entries());
    for (const [key, entry] of entries) {
      if (now > entry.expiresAt) {
        this.memoryStore.delete(key);
        removedCount++;
      }
    }

    if (removedCount > 0) {
      console.log(`Flight anchor cache cleanup: removed ${removedCount} expired entries`);
    }
  }

  /**
   * Evict least recently used entry to make room
   */
  private evictLRU(): void {
    const entries = Array.from(this.memoryStore.entries());
    if (entries.length === 0) return;

    // Sort by timestamp (oldest first)
    entries.sort((a, b) => a[1].timestamp - b[1].timestamp);

    const [oldestKey] = entries[0];
    this.memoryStore.delete(oldestKey);
    console.log(`Flight anchor cache evicted LRU entry: ${oldestKey}`);
  }

  /**
   * Update hit rate metrics
   */
  private updateHitRate(): void {
    if (this.metrics.totalRequests > 0) {
      this.metrics.hitRate = (this.metrics.hits / this.metrics.totalRequests) * 100;
    } else {
      this.metrics.hitRate = 0;
    }
  }

  /**
   * Clear all cache entries
   * Note: Uses TTL expiry for Redis instead of KEYS command for serverless compatibility
   */
  async clear(): Promise<void> {
    // For Redis: rely on TTL expiry rather than KEYS scan which is expensive
    // Just clear the in-memory cache and let Redis entries expire naturally
    this.memoryStore.clear();
    console.log('Flight anchor cache cleared (memory cleared, Redis entries will expire via TTL)');
  }

  /**
   * Cleanup resources
   */
  destroy(): void {
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
    }

    this.memoryStore.clear();
    this.redisClient = null;
    this.isRedisConnected = false;
  }
}

